import axios from 'axios';
import React,{useState, useEffect} from 'react';

import Speaker from'../Speaker/Speaker';
import SpeakerSearchBar from '../SpeakerSearchBar/SpeakerSearchBar';

const Speakers = () => {

  const [searchQuery,setSearchQuery] = useState("");


  function toggleSpeakerFavorite(speakerRec) {
    return {
      ...speakerRec,
      isFavorite: !speakerRec.isFavorite,
    };
  }

  async function onFavoriteToggleHandler(speakerRec) {
    const toggledSpeakerRec = toggleSpeakerFavorite(speakerRec);
    const speakerIndex = speakers
          .map((speaker) => speaker.id)
          .indexOf(speakerRec.id);

    await axios.put(`https://localhost:4000/speakers/${speakerRec.id}`, toggledSpeakerRec);
    setSpeakers
     ([...speakers.slice(0, speakerIndex), toggledSpeakerRec, ...speakers.slice(speakerIndex + 1)]);
  }

  const [speakers, setSpeakers ] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get("https://localhost:4000/speakers").catch((error)=> {
        console.log(error);
    });
      setSpeakers(response.data);
    }
    fetchData();
  })

  return (
    <div>
      <SpeakerSearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
      <div className="grid md:grid-cols-2 lg:grid-cols-3 grid-cols-1 gap-12">
                {speakers
                .filter((rec) => {
                  const targetString = `${rec.firstName} ${rec.lastName}`.toLowerCase();
                  return searchQuery.length === 0 ? true : targetString.includes(searchQuery.toLocaleLowerCase());
                })
                .map((speaker => (
                  <Speaker key={speaker.id} {...speaker} 
                  onFavoriteToggle={() => onFavoriteToggleHandler(speaker) } /> )
                  ))}
      </div>
    </div>
  );
};
export default Speakers;
